import { Meteor } from 'meteor/meteor';

if(Meteor.isServer){
  Employees = new Mongo.Collection('employees');
  Skills = new Mongo.Collection('skills');

  // Publishes the employees collection to the client side.
  Meteor.publish('employees.all', function() {
    return Employees.find();
  });

  // Publishes the existing skills collection to the client side subcscriotion.
  Meteor.publish('employees.skills', function(){
    return Skills.find();
  });


}
