import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';
import { Mongo } from 'meteor/mongo';

import './main.html';
employee_filtered_list = ["n/a"];




if(Meteor.isClient) {
  Employees = new Mongo.Collection('employees');
  Skills = new Mongo.Collection('skills');




  Template.resource_list.onCreated( function() {
      // Subscribe to the client side data publications as soon as the template is created.
      //Meteor.subscribe('employees.all');
      Meteor.subscribe('employees.skills');
      console.log(employee_filtered_list);
      Meteor.subscribe('employees.bySkill', employee_filtered_list);

  });

  Template.resource_list.helpers({
    getEmployees: function(){
      console.log(employee_filtered_list);
      return Employees.find();
    }
});

  Template.resource_filter.helpers({

    getSkills: function(){
      return Skills.find();
    }
  });

  //Event for when the employee_filter form is submitted.
  Template.resource_filter.events({
    'submit .employee_filter': function(event){
      event.preventDefault();
      var filterList = [];
      event.target.skill_box.forEach(function(skill) {
        if(skill.checked == true){
          filterList.push(skill.value);
        }
      });
      //console.log(employee_filtered_list);
      employee_filtered_list = filterList;

      }
    });
}
