import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';
import {Meteor} from 'meteor/meteor'

import './main.html';

if(Meteor.isClient) {
  employees = new Mongo.Collection('employees');

  Template.resource_list.onCreated( function() {
      Meteor.subscribe('employees.all');
  });
  Template.resource_list.helpers({
    getEmployees: function(){
      //console.log(employees.count());
      return employees.find();
    }
  });
}

if(Meteor.isServer){
  employees = Mongo.Collection('employees');
  Meteor.publish('employees.all', function() {
    var self = this;
    return employees.find();
    self.ready();
  });
}
